import React from 'react';
import { Button, TextField, Divider, Card, CardContent, Box, Grid, List, ListItemText } from '@material-ui/core';


class GameWaiting extends React.Component {
	constructor(props){
		super(props);

		this.socket = props.socket;
		this.game = props.game;
		this.me = props.me;

		this.state = {gameCode: this.game.gameCode, players: this.game.players};
	}

	componentDidMount(){
		this.socket.emit("userSync", this.me.name);

		this.socket.on("gameUpdated", game => {
			this.game = game;
			this.setState((state, props) => ({
				players: this.game.players
			}));
		});
	}

	handleStartGame(){
	}

	render(){
		return (
			<Grid container style={{minHeight: '100vh'}} direction='row' justify='center' alignItems='center'>
			<Grid item style={{width: '80vw'}}><Box display='flex' justify='center' alignItems='center' bgColor='blue' width='80vw'>
				<Card style={{width: "25vw"}}><CardContent>
					<Grid container direction='column' justify='space-between' alignItems='center' style={{height: '45vh'}}>
						<Grid item>
							<h1 style={{color: 'salmon'}}>{this.state.gameCode.slice(0, 3) + "-" + this.state.gameCode.slice(3)}</h1>
						</Grid>

						<Grid item>
							<List component="nav">
								{this.state.players.map((item, index) => <ListItemText key={item.id} primary={item.name + (item.id === this.me.uid ? " (You)" : "")}/>)}
							</List>
						</Grid>

						<Grid item>
							<Button color='primary' variant='contained'>Start Game</Button>
						</Grid>
					</Grid>
				</CardContent></Card>
			</Box></Grid>
			<Grid item style={{width: '20vw'}}>
				<Grid container direction='column' justify='space-between' alignItems='center' style={{height: '45vh'}}>
					<Grid item>
						<h1 style={{color: 'salmon'}}>{this.state.gameCode.slice(0, 3) + "-" + this.state.gameCode.slice(3)}</h1>
					</Grid>

					<Grid item>
						<List component="nav">
							{this.state.players.map((item, index) => <ListItemText key={item.id} primary={item.name + (item.id === this.me.uid ? " (You)" : "")}/>)}
						</List>
					</Grid>

					<Grid item>
						<Button color='primary' variant='contained'>Start Game</Button>
					</Grid>
				</Grid>
			</Grid>
			</Grid>
		);
	}
}

export default GameWaiting;
