import React from 'react';
import { Button, TextField, Divider, Card, CardContent, Box, Grid, CssBaseline, InputAdornment, ClearIcon } from '@material-ui/core';


class GameWaiting extends React.Component {
	constructor(props){
		super(props);

		console.log("game waiting");

		this.socket = props.socket;
		this.gameCode = props.gameCode

		this.playerNames = [];
		this.socket.on("playersUpdated", playerNames => {
			this.playerNames = playerNames;
			console.log(this.gameCode);
		});

		this.state = {playerNames: []};
	}

	handleStartGame(){
	}

	render(){
		return (
			<Grid container style={{minHeight: '100vh'}} direction='column' justify='center' alignItems='center'><Grid item>
			<Card style={{width: "50vh", height: "50vh"}}><CardContent>
				<Grid container direction='column' justify='space-between' alignItems='center'>
					<Grid item xs>
						<h1 style={{color: 'salmon'}}>{this.gameCode.slice(0, 3) + "-" + this.gameCode.slice(3)}</h1>
					</Grid>

					<Grid item xs>
						<Button color='primary' variant='contained'>Start Game</Button>
					</Grid>
				</Grid>
			</CardContent></Card>
			</Grid></Grid>
		);
	}
}

export default GameWaiting;
