import React from 'react';
import InputMask from "react-input-mask";
import { makeStyles } from '@material-ui/core/styles';
import { Button, TextField, Divider, Card, CardContent, Box, Grid, CssBaseline, InputAdornment, ClearIcon } from '@material-ui/core';
import openSocket from 'socket.io-client';

const socket = openSocket('http://localhost:5000');

class App extends React.Component {
	constructor(props){
		super(props);

		console.log("App created");

		this.gameCodes = [];
		socket.on("gamesChanged", gameCodes => this.gameCodes = gameCodes);

		this.state = {name: '', gameCode: '', nameError: false, gameCodeError: false, gameCodeErrorText: ''};
	}

	handleCreateGame(){
		console.log("Create game");
		if (this.state.name.trim() === ''){
			this.setState((state, props) => ({
				nameError: true
			}));
			return;
		}
		console.log("Creating game");
	}

	handleJoinGame(){
		const nameValid = this.state.name.trim() !== '';
		const codeValid = this.state.gameCode.length === 6;
		const gameExists = codeValid && this.gameCodes.includes(this.state.gameCode);

		if (nameValid && gameExists){
			console.log("Joining game");
			return;
		}

		this.setState((state, props) => ({
			nameError: !nameValid,
			gameCodeError: !gameExists,
			gameCodeErrorText: codeValid ? "This game doesn't exist" : ""
		}));
	}

	handleFieldUpdate(e){
		let { name, value } = e.target;
		console.log(name + "  " + value);
		if (name === 'gameCode')
			value = value.replace('-', '');
		this.setState((state, props) => ({
			[name]: value
		}));
	}

	render(){
		return (
			<CssBaseline>
			<Grid container style={{minHeight: '100vh'}} direction='column' justify='center' alignItems='center'><Grid item>
			<Card style={{width: "fit-content"}}><CardContent>
				<Box display='flex' flexDirection='column' alignItems='center' width="fit-content" p={2}>
					<Box m={3}>
						<TextField name="name" error={this.state.nameError} onChange={e => this.handleFieldUpdate(e)} label="Name" variant="outlined"/>
					</Box>

					<Grid container direction='row' justify='center' alignItems='center'>
						<Grid item xs>
							<Box display='flex' justifyContent="flex-end" mr={4}>
								<Button onClick={() => this.handleCreateGame()} variant='contained' color='primary'>Create Game</Button>
							</Box>
						</Grid>
						<Grid item xs>
							<Box display='flex' flexDirection='column' alignSelf='center' py={3} pl={4} borderLeft={1} borderColor="grey.300">
								<InputMask mask="999-999" maskChar=" " onChange={e => this.handleFieldUpdate(e)}>{
									() => <TextField
										name="gameCode"
										error={this.state.gameCodeError}
										errorText={this.state.gameCodeErrorText}
										onClick={e => this.handleFieldUpdate(e)}
										label="Game Code" variant="outlined" type="search"
										/>
								}</InputMask>
								<Box my={0.5}/>
								<Button onClick={() => this.handleJoinGame()} variant='contained' color='primary'>Join Game</Button>
							</Box>
						</Grid>
					</Grid>
				</Box>
			</CardContent></Card>
			</Grid></Grid>
			</CssBaseline>
		);
	}
}

export default App;
