import React from 'react';
import { Redirect } from 'react-router';
import { Button, TextField, Divider, Card, CardContent, Box, Grid, List, ListItemText, IconButton } from '@material-ui/core';
import ArrowBackIcon from '@material-ui/icons/ArrowBack'
import Chat from './Chat.js';

class Client extends React.Component {
	constructor(props){
		super(props);

		this.socket = props.socket;
		this.game = props.game;
		this.me = props.me;

		this.board = []
		for (let r=0; r<10; r++){
			let row = [];
			for (let c=0; c<11; c++)
				row.push('');
			this.board.push(row);
		}

		let pos = {x: 0, y: 9};
		let colors = ['b', 'g', 'r', 'y'];
		for (let count = 5; count > 1; count--)
			for (let color = 0; color < 5; color++)
				for (let c=0; c<count; c++){
					console.log(this.board[pos.y][pos.x]);
					console.log(colors[color]);
					this.board[pos.y][pos.x] = colors[color];
					pos = this.getNextPos(pos);
				}
		console.log(this.board);
	}

	componentDidMount(){
	}

	getNextPos(pos){
		let {x, y} = pos;
		if (x === y && x !== 6 && x !== 9)
			return {x: x+1, y: y};
		if (x === y+1 && x !== 1 && x !== 3)
			return {x: x-1, y: y};

		if (x === 0 || x === 2 || x === 4)
			return {x: x, y: y-1};
		if (x === 7 || x === 10)
			return {x: x, y: y+1};
		if (y === 0 || y === 2)
			return {x: x+1, y: y};
		if (y === 6 || y === 9)
			return {x: x-1, y: y};
	}

	render(){
		return (
			<Grid container style={{minHeight: '100vh'}} direction='row' justify='center' alignItems='center'>
				<Grid item style={{width: '80%'}} align='center'>
					{[...Array(10)].map((row, y) => (
						<Grid container direction='row' key={"row." + y} justify='center' alignItems='center' style={{height: '5vh'}}>
							{[...Array(11)].map((col, x) => (
								<Grid item key={"row." + y + ".col." + x} style={{width: '5vh', height: '100%'}}>
									<Box border={1} style={{width: '100%', height: '100%'}}/>
								</Grid>
							))}
						</Grid>
					))}
				</Grid>

				<Grid item style={{width: '20%'}}>
					<Chat socket={this.socket} messages={this.game.messages} me={this.me}/>
				</Grid>
			</Grid>
		);
	}
}

export default Client;
