import React from 'react';
import { Redirect } from 'react-router';
import { Button, TextField, Divider, Card, CardContent, Box, Grid, List, ListItemText, IconButton } from '@material-ui/core';
import Chat from './Chat.js';
import CCIcon from '@material-ui/icons/Eco';
import CultIcon from '@material-ui/icons/SupervisorAccount';
import SysIcon from '@material-ui/icons/Settings';
import FinIcon from '@material-ui/icons/AccountBalance';
import Square from './Square.js';
import Die from './Die.js';
import Consts from './Consts.js';

class Client extends React.Component {
	constructor(props){
		super(props);

		this.socket = props.socket;
		this.game = props.game;
		this.me = props.me;

		Square.generateLocations();

		this.state = {playersSquares: [0, 0, 0, 0], dieNumber: 6, card: {type: 0, text: "The farm is dead", offset: "-5"}, yourCard: false, yourTurn: false};
	}

	componentDidMount(){
	}

	async rollDie(n) {
		if (!this.state.yourTurn)
			return;

		let lastNumber = 0;
		for (let c=0; c<10; c++){
			let number = lastNumber;
			while (number === lastNumber)
				number = Math.floor(Math.random() * 6) + 1;
			this.setState((state, props) => ({
				dieNumber: number
			}));
			await new Promise(resolve => setTimeout(resolve, 100));
		}

		this.setState((state, props) => ({
			dieNumber: n
		}));
	}

	render(){
		return (
			<Grid container key='m' style={{minHeight: '100vh', minWidth: '100vw'}} direction='row' justify='center' alignItems='center'>
				<Grid container key='ms' style={{width: '80%', minHeight: '100vh'}} alignItems='center' justify='center'>
					<Grid container key='msc' style={{width: 'calc(40vw - 44vh)'}} direction='column' alignItems='center'>
						{this.renderCards()}
					</Grid>

					<Grid item key='msb'>
						{this.renderBoard()}
					</Grid>

					{this.renderRight()}
				</Grid>

				<Grid item key='mchat' style={{width: '20%'}}>
					<Chat socket={this.socket} messages={this.game.messages} me={this.me}/>
				</Grid>
			</Grid>
		);
	}

	renderCards(){
		return [...Array(4).keys()].map(card => (
			<Grid item key={'msc.' + card}>
				<Box bgcolor={Consts.COLORS[Consts.ORDER[card]]} style={{height: '18vh', width: '12vh'}} m='2vh' border={2} borderRadius={10}
					alignItems='center' justifyContent='center' display='flex'>

					{card === 0 ? <SysIcon style={{width: '6vh', height: '6vh'}}/> :
						card === 1 ? <FinIcon style={{width: '6vh', height: '6vh'}}/> :
						card === 2 ? <CCIcon style={{width: '6vh', height: '6vh'}}/> :
						card === 3 ? <CultIcon style={{width: '6vh', height: '6vh'}}/> : null}
				</Box>
			</Grid>
		));
	}

	renderRight(){
		return (
			<Grid container key='msr' style={{width: 'calc(40vw - 44vh)'}} alignContent='center' alignItems='center' direction='column'>
				<Grid item key='msrd'>
					<div onClick={() => this.rollDie(5)}>
						<Die number={this.state.dieNumber}/>
					</div>
				</Grid>

				<Grid item key='msrc'>
					{this.state.card ? this.renderYourCard() : this.renderCardSpace()}
				</Grid>
			</Grid>
		);
	}

	renderCardSpace(){
		return (<Box style={{height: '18vh', width: '12vh'}} m='2vh' border={2} borderRadius={10} m={10} bgcolor='rgba(0, 0, 0, 0.1)'/>);
	}

	renderYourCard(){
		let {type, text, offset} = this.state.card;
		return (
			<Box style={{height: '18vh', width: '12vh'}} m='2vh' border={2} borderRadius={10} m={10}>
				<Grid container direction='column' alignItems='center'>
					<Grid item><Box p={1}>
						{type === 0 ? <SysIcon style={{width: '3vh', height: '3vh', color: Consts.COLORS[Consts.SYS]}}/> :
							type === 1 ? <FinIcon style={{width: '3vh', height: '3vh', color: Consts.COLORS[Consts.FIN]}}/> :
							type === 2 ? <CCIcon style={{width: '3vh', height: '3vh', color: Consts.COLORS[Consts.CC]}}/> :
							type === 3 ? <CultIcon style={{width: '3vh', height: '3vh', color: Consts.COLORS[Consts.CULT]}}/> : null}
					</Box></Grid>

					<Grid item>
						<Box p={1} display='flex' justifyContent='center'>{text}</Box>
					</Grid>
				</Grid>
			</Box>
		);
	}

	renderBoard(){
		return [...Array(10).keys()].map(y => (
			<Grid container direction='row' key={"y." + y} justify='center' alignItems='center' style={{height: Consts.BOX_S}}>
				{[...Array(11).keys()].map(x => (
					<Square key={"sq." + y + "." + x} loc={{x: x, y: y}} playersSquares={this.state.playersSquares}/>
				))}
			</Grid>
		));
	}
}

export default Client;
