import React from 'react';
import { Redirect } from 'react-router';
import { Button, TextField, Divider, Card, CardContent, Box, Grid, List, ListItemText, IconButton } from '@material-ui/core';
import ArrowBackIcon from '@material-ui/icons/ArrowBack'
import Chat from './Chat.js';

class Client extends React.Component {
	constructor(props){
		super(props);

		this.socket = props.socket;
		this.game = props.game;
		this.me = props.me;

		this.board = []
		for (let r=0; r<10; r++){
			let row = [];
			for (let c=0; c<11; c++)
				row.push('white');
			this.board.push(row);
		}

		let pos = {x: 0, y: 9};
		let colors = ['blue', 'green', 'red', 'yellow'];
		for (let count = 5; count > 1; count--)
			for (let color = 0; color < 4; color++)
				for (let c=0; c<count; c++){
					this.board[pos.y][pos.x] = colors[color];
					pos = this.getNextPos(pos);
				}
		console.log("board done");
	}

	componentDidMount(){
	}

	getNextPos(pos){
		let {x, y} = pos;
		let down = {x: x, y: y+1};
		let up = {x: x, y: y-1};
		let right = {x: x+1, y: y};
		let left = {x: x-1, y: y};
		if ((x === 0 && y === 0) || (x === 2 && y === 2))
			return right;
		if ((x === 10 && y === 0) || (x === 7 && y === 2))
			return down;
		if ((x === 10 && y === 9) || (x === 7 && y === 6))
			return left;
		if ((x === 2 && y === 9) || (x === 4 && y === 6))
			return up;

		if (x === 0) return up;
		if (y === 0) return right;
		if (x === 10) return down;
		if (y === 9) return left;
		if (x === 2) return up;
		if (y === 2) return right;
		if (x === 7) return down;
		if (y === 6) return left;

		console.log("Unhandled ", pos);
	}

	render(){
		const board = this.board;
		console.log(board);
		return (
			<Grid container style={{minHeight: '100vh'}} direction='row' justify='center' alignItems='center'>
				<Grid item style={{width: '80%'}} align='center'>
					{[...Array(10)].map(this.rowMap)}
				</Grid>

				<Grid item style={{width: '20%'}}>
					<Chat socket={this.socket} messages={this.game.messages} me={this.me}/>
				</Grid>
			</Grid>
		);
	}

	rowMap = (row, y) => {
		console.log(this.board[row]);
		return (
			<Grid container direction='row' key={"row." + row} justify='center' alignItems='center' style={{height: '5vh'}}>
				{[...Array(11)].map((col, x) => this.colMap(row, col))}
			</Grid>
		);
	}

	colMap = (row, col) => {
		console.log(this.board[row][col]);
		return (
			<Grid item key={"row." + row + ".col." + col} style={{width: '5vh', height: '100%'}}>
				<Box border={1} style={{width: '100%', height: '100%'}} bgcolor={this.board[row][col]}/>
			</Grid>
		);
	}
}

export default Client;
