import React from 'react';
import InputMask from "react-input-mask";
import { makeStyles } from '@material-ui/core/styles';
import { Button, TextField, Divider, Card, CardContent, Box, Grid, CssBaseline, InputAdornment, ClearIcon } from '@material-ui/core';
import openSocket from 'socket.io-client';

class App extends React.Component {
	constructor(props){
		super(props);

		this.gameCodes = [];
		this.socket = openSocket('http://localhost:5000');
		this.socket.on("gamesChanged", gameCodes => this.gameCodes = gameCodes);

		this.state = {name: '', gameCode: '', nameError: false, gameCodeError: false};
	}

	handleCreateGame(){
		console.log("handling create game");
		if (this.state.name.trim() === ''){
			this.setState((state) => ({
				nameError: true
			}));
			return;
		}
		console.log("Creating game");
	}

	handleJoinGame(){
		const nameValid = this.state.name.trim() === '';
		const codeValid = this.state.gameCode.length() === 6;
		const gameExists = codeValid && this.gameCodes.contains(this.state.gameCode);

		if (nameValid && gameExists){
			console.log("Joining game");
			return;
		}

		this.setState((state) => ({
			nameError: nameValid,
			gameCodeError: gameExists
		}));
	}

	render(){
		return (
			<CssBaseline>
			<Grid container style={{minHeight: '100vh'}} direction='column' justify='center' alignItems='center'><Grid item>
			<Card style={{width: "fit-content"}}><CardContent>
				<Box display='flex' flexDirection='column' alignItems='center' width="fit-content" p={2}>
					<Box m={3}>
						<TextField error={this.state.nameError} label="Name" variant="outlined"/>
					</Box>

					<Grid container direction='row' justify='center' alignItems='center'>
						<Grid item xs>
							<Box display='flex' justifyContent="flex-end" mr={4}>
								<Button onClick={this.handleCreateGame} variant='contained' color='primary'>Create Game</Button>
							</Box>
						</Grid>
						<Grid item xs>
							<Box display='flex' flexDirection='column' alignSelf='center' py={3} pl={4} borderLeft={1} borderColor="grey.300">
								<InputMask mask="999-999" maskChar=" ">{
									() => <TextField error={this.state.gameCodeError}label="Game Code" variant="outlined" type="search"/>
								}</InputMask>
								<Box my={0.5}/>
								<Button onClick={this.handleJoinGame} variant='contained' color='primary'>Join Game</Button>
							</Box>
						</Grid>
					</Grid>
				</Box>
			</CardContent></Card>
			</Grid></Grid>
			</CssBaseline>
		);
	}
}

export default App;
