import React from 'react';
import { Redirect } from 'react-router';
import { Button, TextField, Divider, Card, CardContent, Box, Grid, List, ListItemText, IconButton } from '@material-ui/core';
import { red, yellow, lightGreen, blue, purple } from '@material-ui/core/colors';
import ArrowBackIcon from '@material-ui/icons/ArrowBack'
import Chat from './Chat.js';
import BarnLogo from './barn.svg';
import CarrotLogo from './carrot.svg';
import MilkLogo from './milk.svg';
import RakeLogo from './rake.svg';

class Client extends React.Component {
	constructor(props){
		super(props);

		this.socket = props.socket;
		this.game = props.game;
		this.me = props.me;

		this.locations = [];
		let pos = {x: 0, y:10};
		while (true){
			this.locations.push(pos);
			pos = this.getNextPos(pos);
			if (pos.x === 4 && pos.y === 5){
				this.locations.push(pos);
				break;
			}
		}

		this.board = [];
		for (let r=0; r<11; r++){
			let row = [];
			for (let c=0; c<11; c++)
				row.push('transparent');
			this.board.push(row);
		}

		let colors = [blue[400], lightGreen[700], red[500], yellow[500]];
		let color = 0;
		let currentCount = 5;
		let count = 0;
		for (let c=1; c<this.locations.length-1; c++){
			let location = this.locations[c];
			this.board[location.y][location.x] = colors[color];
			count++;
			if (count === currentCount){
				color++;
				count = 0;
			}

			if (color === 4){
				color = 0;
				currentCount--;
			}
		}

		this.board[5][4] = purple[200];
		this.board[10][0] = purple[200];

		this.state = {playerLocations: [0, 0, 0, 0]};
	}

	componentDidMount(){
	}

	getNextPos(pos){
		let vector = this.getNextPosVector(pos);
		return {x: pos.x + vector.x, y: pos.y + vector.y};
	}

	getNextPosVector(pos){
		let {x, y} = pos;
		let down = {x: 0, y: 1};
		let up = {x: 0, y: -1};
		let right = {x: 1, y: 0};
		let left = {x: -1, y: 0};
		if ((x === 0 && y === 0) || (x === 2 && y === 2))
			return right;
		if ((x === 10 && y === 0) || (x === 7 && y === 2))
			return down;
		if ((x === 10 && y === 9) || (x === 7 && y === 6))
			return left;
		if ((x === 2 && y === 9) || (x === 4 && y === 6))
			return up;

		if (x === 0) return up;
		if (y === 0) return right;
		if (x === 10) return down;
		if (y === 9) return left;
		if (x === 2) return up;
		if (y === 2) return right;
		if (x === 7) return down;
		if (y === 6) return left;

		console.log("Unhandled ", pos);
	}

	render(){
		return (
			<Grid container style={{minHeight: '100vh'}} direction='row' justify='center' alignItems='center'>
				<Grid item style={{width: '80%'}} align='center'>
					{[...Array(11).keys()].map(row => (
						<Grid container direction='row' key={"row." + row} justify='center' alignItems='center' style={{height: '8vh'}}>
							{[...Array(11).keys()].map(col => this.renderSquare(row, col))}
						</Grid>
					))}
				</Grid>

				<Grid item style={{width: '20%'}}>
					<Chat socket={this.socket} messages={this.game.messages} me={this.me}/>
				</Grid>
			</Grid>
		);
	}

	renderSquare(row, col){
		let color = this.board[row][col];
		return (<Grid item key={"row." + row + ".col." + col} style={{width: '8vh', height: '100%'}}>
			<Box border={(color === 'transparent') ? 0 : 1} style={{width: '100%', height: '100%'}} bgcolor={color} p={0.5}>
				{this.renderPlayerIcons(this.state.playerLocations, row, col)}
			</Box>
		</Grid>);
	}

	renderPlayerIcons(playerLocations, row, col){
		console.log(playerLocations);
		let playerHere = [];
		for (let playerLocation of playerLocations){
			console.log(playerLocation);
			playerHere.push(this.locations[playerLocation].x === col && this.locations[playerLocation].y === row);
		}
		console.log("Player here " + row + ", " + col + ": ", playerHere);

		return (
			<Grid container style={{width: '100%', height: '100%'}}>
				<Grid container direction='row' style={{width: '100%', height: '50%'}}>
					<Grid item style={{width: '50%', height: '100%'}}>
						<img src={playerHere[0] && BarnLogo}/>
					</Grid>

					<Grid item style={{width: '50%', height: '100%'}}>
						<img src={playerHere.length > 1 && playerHere[1] && CarrotLogo}/>
					</Grid>
				</Grid>

				<Grid container direction='row' style={{width: '100%', height: '50%'}}>
					<Grid item style={{width: '50%', height: '100%'}}>
						<img src={playerHere.length > 2 && playerHere[2] && RakeLogo}/>
					</Grid>

					<Grid item style={{width: '50%', height: '100%'}}>
						<img src={playerHere.length > 3 && playerHere[3] && MilkLogo}/>
					</Grid>
				</Grid>
			</Grid>
		);
	}
}

export default Client;
