import React from 'react';
import { Button, TextField, Divider, Card, CardContent, Box, Grid } from '@material-ui/core';


class GameWaiting extends React.Component {
	constructor(props){
		super(props);

		this.socket = props.socket;
		this.gameCode = props.gameCode

		this.players = [];
		this.state = {players: []};
	}

	componentWillMount(){
		this.socket.on("playersUpdated", players => {
			this.players = players;
			console.log(this.players);
			this.setState((state, props) => ({
				players: this.players
			}));
		});
	}

	handleStartGame(){
	}

	render(){
		return (
			<Grid container style={{minHeight: '100vh'}} direction='column' justify='center' alignItems='center'><Grid item>
			<Card style={{width: "50vh"}}><CardContent>
				<Grid container direction='column' justify='space-between' alignItems='center' style={{height: '45vh'}}>
					<Grid item>
						<h1 style={{color: 'salmon'}}>{this.gameCode.slice(0, 3) + "-" + this.gameCode.slice(3)}</h1>
					</Grid>

					<Grid item>
						{this.state.players.map((item, index) => (
							<li key={item.id}>{item.name}</li>
						))};
					</Grid>

					<Grid item>
						<Button color='primary' variant='contained'>Start Game</Button>
					</Grid>
				</Grid>
			</CardContent></Card>
			</Grid></Grid>
		);
	}
}

export default GameWaiting;
