import React from 'react';
import { Grid, TextField, Divider, List, ListItem, ListSubheader, ListItemText, InputAdornment, IconButton } from '@material-ui/core';
import SendIcon from '@material-ui/icons/Send';

class Chat extends React.Component {
	constructor(props){
		super(props);

		this.socket = props.socket;
		console.log(props.messages);
		this.messages = this.formatMessages(props.messages);
		this.me = props.me;

		this.state = {messages: this.messages, sendMessage: ''};
	}

	componentDidMount(){
		this.socket.emit("userSync", this.me.name);

		this.socket.on("messagesUpdated", messages => {
			this.messages = this.formatMessages(messages);
			this.setState((state, props) => ({
				messages: this.messages
			}));
		});
	}

	formatMessages(messages){
		let newMessages = new Array();
		for (let message of messages){
			console.log(message);
			if (newMessages.length === 0 || (message.senderName != newMessages[newMessages.length-1].senderName))
				newMessages.push({lines: [message.message], senderName: message.senderName, serverMessage: message.serverMessage})
			else
				newMessages[newMessages.length-1].lines.push(message.message);
		}
		return newMessages;
	}

	messageChanged = (e) => {
		e.persist();
		this.setState((state, props) => ({
			sendMessage: e.target.value
		}));
	}

	handleSend = () => {
		console.log("sending message", this.state.sendMessage);
		this.socket.emit("sendMessage", this.state.sendMessage);
		this.setState((state, props) => ({
			sendMessage: ''
		}));
	}

	render(){
		return (
			<Grid container direction='column' justify='space-between' style={{width: "100%", height: "100%"}}>
				<Grid item>
					<List dense style={{overflow: 'auto'}}>{this.state.messages.map(this.renderItem)}</List>
				</Grid>
				<Grid item>
					<TextField value={this.state.sendMessage} placeholder="Send a message..." variant="outlined" onChange={this.messageChanged} InputProps={{
						endAdornment: <InputAdornment position='end'>
								<IconButton onClick={this.handleSend}>
								<SendIcon/>
							</IconButton>
						</InputAdornment>
					}}/>
				</Grid>
			</Grid>
		);
	}

	renderItem = (sItem, sIndex) => {
		const senderName = (sItem.senderName === this.me.name ? "You" : sItem.senderName);
		return (
			<li key={`section-${sIndex}`}>
				<ul style={{padding: "0"}}>
					<ListSubheader>{senderName}</ListSubheader>
					{sItem.lines.map((item, index) => (
						<ListItem key={`item-${sIndex}-${index}`}>
							<ListItemText primary={item}/>
						</ListItem>
					))}
					<Divider/>
				</ul>
			</li>
		);
	}
}

export default Chat;
